function Books() {
  //filter books based on status
  const filterBooksByStatus = (status) => {
    return booksData.filter((book) => book.status === status);
  };

  const [maxHeight, setMaxHeight] = useState(0);

  useEffect(() => {
    // Calculate the maximum height among all book cards
    const cardElements = document.querySelectorAll(".book-card");
    let max = 0;
    cardElements.forEach((card) => {
      max = Math.max(max, card.offsetHeight);
    });
    setMaxHeight(max);
  }, []);
  return (
    <DefaultLayout>
      <div id="books" className="books-container">
        <div className="">
        <div className="empty-div">
          <h1 className="gradient-text header-container">Books</h1>
          <header className="header-container">
            <h3 className="black-bold">Some of My Recent readings</h3>
          </header>
          <p className="par-content">
          Exploring worlds within pages, one story at a time – discover literary treasures on my personal bookshelf
          </p>
          </div>

          {/* Book Cards */}
          <div className="row row1" 
          // style={{ paddingTop: "20px" }}
          >
            <div className="col">
              <h5 className="category1">To Read</h5>
              {filterBooksByStatus("To Read").map((book) => (
                <BookCard key={book.id} {...book} />
              ))}
            </div>
            <div className="col">
              <h5 className="category1">Reading</h5>
              {filterBooksByStatus("Reading").map((book) => (
                <BookCard key={book.id} {...book} />
              ))}
            </div>
            <div className="col">
              <h5 className="category1">Already Read</h5>
              {filterBooksByStatus("Already Read").map((book) => (
                <BookCard key={book.id} {...book} />
              ))}
            </div>
          </div>
        </div>
      </div>
    </DefaultLayout>
  );
}

export default Books;
